import "./write.css";
import { useState, useContext } from "react";
import axios from "axios";
import { Context } from "../../../context/Context";

export default function Write() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [file, setFile] = useState(null);
  const { user } = useContext(Context);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newPost = {
      username: user.username,
      title,
      description,
    };
    if (file) {
      const data = FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      newPost.photo = filename;
      try {
        await axios.post("/upload", data);
      } catch (err) {}
    }
    try {
      const res = axios.post("/posts", newPost);
      window.location.replace("/post/" + res.data._id);
    } catch (err) {}
  };

  return (
    <div className="write">
      {file && (
        <img
          className="write--img"
          src={URL.createObjectURL(file)}
          alt=""
        />
      )}
      <form className="write--form" onSubmit={handleSubmit}>
        <div className="write--form-group">
          <label htmlFor="file--input">
            <i class="write--icon fa-solid fa-plus"></i>
          </label>
          <input type="file" id="file--input" style={{ display: "none" }} onChange={e=>setFile(e.target.files[0])} />
          <input
            type="text"
            placeholder="Title"
            className="write--input"
            autoFocus={true}
          />
        </div>
        <div className="write--form-group">
          <textarea
            placeholder="Content goes here..."
            type="text"
            className="write--input write--text"
          ></textarea>
        </div>
        <button className="write--submit-btn" type="submit">
          Publish
        </button>
      </form>
    </div>
  );
}
