import { Link, useLocation } from "react-router-dom"
import "./singlePost.css"
import { useEffect, useState, useContext } from 'react';
import { Context } from "../../context/Context";
import axios from 'axios';

export default function SinglePost() {
    const location = useLocation();
    const path = location.pathname.split("/")[2];
    const [post, setPost] = useState({});
    const PF = "localhost:5000/images/";
    const { user } = useContext(Context);

    useEffect(() => {
        const getPost = async ()=>{
            const res = await axios.get("/posts/" + path);
            setPost(res.data);
            // console.log(location)
        }

        getPost();
    },[path])


  return (
    <div className='singlePost'>
        <div className="singlePostWrapper">
            {post.photo && 
            <img
            className="singlePost--image"
            src={PF + post.photo}
            alt=''
            />
            }
            <h1 className="singlePost--title">
                {post.title}
                {post.username === user?.username && (
                <div className="singlePost--edit">
                      <i className="singlePost--icon fa-solid fa-pen-to-square"></i>
                      <i className="singlePost--icon fa-solid fa-trash"></i>
                </div>
                )}
            </h1>
            <div className="singlePost--info">
                <span className="singlePost--author">
                    Author: 
                    <Link to={`/?user=${post.username}`} className="link">
                    <b>{post.username}</b>
                    </Link>
                </span>
                <span className="singlePost--date">{new Date(post.createdAt).toDateString()}</span>
            </div>
            <p className="singlePost--description">
                {post.description}
            </p>
        </div>
    </div>
  )
}
