import './sidebar.css'
import { useState, useEffect } from 'react';
import axios from 'axios';


export default function Sidebar() {
  const [category, setCategory] = useState([]);

  useEffect(() => {
    const getCategory = async () => {
      const res = await axios.get("/categories")
      setCategory(res.data);
    }
    console.log(getCategory());
  }, [])


  return (
    <div className='sidebar'>
      <div className='sidebar--item'>
        <span className="sidebar--title">ABOUT ME</span>
        <img
          className="sidebar--picture"
          src="https://scontent-atl3-2.xx.fbcdn.net/v/t1.6435-9/158432679_4043535989010946_2862607138705131839_n.jpg?_nc_cat=108&ccb=1-7&_nc_sid=09cbfe&_nc_ohc=i6mwfqbyYdAAX-F0TrK&_nc_ht=scontent-atl3-2.xx&oh=00_AfBxNqzRQ5ov8zpXrLh6vhUy3qNxRDIlpIVksyvU_6FlFA&oe=638C2885"
          alt="profile pic" 
          />
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolore 
          neque nulla placeat sequi?
        </p>
      </div>
      <div className="sidebar--item">
        <span className="sidebar--title">CATEGORIES</span>
        <ul className="sidebar--list">


        </ul>
      </div>
      <div className="sidebar--item">
        <span className="sidebar--title">FOLLOW ME</span>
        <div className="sidebar--social">
          <i className="sidebar--icon fa-brands fa-facebook"></i>
          <i className="sidebar--icon fa-brands fa-instagram"></i>
          <i className="sidebar--icon fa-brands fa-youtube"></i>
        </div>

      </div>
    </div>
  )
}
