import "./post.css"
import { Link } from "react-router-dom";

export default function Post({post}) {
  return (
    <div className="post">

        {post.photo && (
            <img
            className="post--image"
            src="https://images.pexels.com/photos/326502/pexels-photo-326502.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2"
            alt=""
            />
        )}

        <div className="post--info">
            <div className="post--info-catagories">
                {post.categories.map((cat) => (
                    <span className="post--info-catagorie" key={post.id}>{cat.name}</span>
                ))}
            </div>
            <Link className="link" to={`/post/${post._id}`}>
                <span className="post--info-title">
                {post.title}
            </span>
            </Link>
            {/* <hr/> come back and fix */}
            <span className="post--info-date">
                {new Date(post.createdAt).toDateString()}
            </span>
        </div>
        <p className="post--description">
            {post.description}
        </p>
    </div>
  )
}
