import { useLocation } from "react-router-dom"
import "./singlePost.css"
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function SinglePost() {
    const location = useLocation();
    const path = location.pathname.split("/")[2];
    const [post, setPost] = useState({});

    useEffect(() => {
        const getPost = async ()=>{
            const res = await axios.get("/posts/" + path);
            setPost(res.data);
        }
        getPost();
    })


  return (
    <div className='singlePost'>
        <div className="singlePostWrapper">
            <img
            className="singlePost--image"
            src="https://www.agilitypr.com/wp-content/uploads/2020/02/technology-1-1.jpg"
            alt=''
             />
            <h1 className="singlePost--title">
                {post.title}
                <div className="singlePost--edit">
                      <i className="singlePost--icon fa-solid fa-pen-to-square"></i>
                      <i className="singlePost--icon fa-solid fa-trash"></i>
                </div>
            </h1>
            <div className="singlePost--info">
                <span className="singlePost--author">
                    Author: <b>Nathan</b>
                </span>
                <span className="singlePost--date">1 hour ago</span>
            </div>
            <p className="singlePost--description">
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Exercitationem, possimus commodi neque, necessitatibus error velit dolor, minima optio minus facere fugiat cupiditate soluta! Mollitia sunt rem consequuntur ratione aut soluta. Lorem ipsum dolor sit amet, consectetur adipisicing elit. Totam sapiente nulla quos quaerat? Dignissimos aliquid, quod laborum quia est harum architecto sunt porro pariatur quas, tempore exercitationem et iure odit? Lorem ipsum dolor sit amet consectetur, adipisicing elit. Soluta eligendi doloribus vel consectetur beatae deserunt animi recusandae dolor autem, adipisci labore numquam tempore excepturi optio earum similique omnis quas veniam. Lorem ipsum dolor sit amet consectetur, adipisicing elit. Tempore voluptatum sequi velit nostrum facere eum excepturi, voluptatem blanditiis distinctio saepe, veritatis reiciendis nam consectetur corporis consequatur quos maiores laudantium omnis! Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolores, obcaecati cupiditate maiores, est quos, ad voluptas ratione omnis ducimus autem laborum officiis earum at illum libero nihil ipsam molestias corrupti.
